    $exceptions = array(
      'hawaii',
      'maryland',
      'vermont',
      'new-hampshire',
      'massachusetts',
      'new-jersey',
      'connecticut',
      'delaware',
      'rhode-island'
    );

    $cur_state = '';
    $active_state_class = 'd-none';

    if(is_singular('location')){
      $cur_state = get_post_field('post_name');

      if(!in_array($cur_state, $exceptions)){
        $active_state_class = 'active ' . $cur_state;
      }
    }

    $locations = new WP_Query(array(
      'post_type' => 'location', 
      'posts_per_page' => -1,
      'post_status' => 'publish'
    ));

    $cai_locations = array();
    if($locations->have_posts()){
      while($locations->have_posts()){
        $locations->the_post();

        $cai_locations[] = sanitize_title(get_the_title());
      }
    }
    wp_reset_postdata();
  ?>

